package com.Quoandrem;

public class FindQuoRemMethod1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int divended = 34;
		int divisor = 3;
		int Quotient = divended/divisor;
		int remainder = divended - (divisor*Quotient);
		
		System.out.println("Get Quotient of Number: " + Quotient);
		System.out.println("Get Remainder of Number: " + remainder);
		
		
		
	}

}
